mapper.select:
SELECT CHALLENGE_ID AS challengeId, USER_ID AS userId, STATUS AS status, START_DATE AS startDate, END_DATE AS endDate FROM MYCHALL WHERE USER_ID = #{userId}
AND CHALLENGE_ID IN ( SELECT CHALLENGE_ID FROM CHALLENGES WHERE CHALLENGE_MODE = #{challengeMode} )
AND ( STATUS = 'PROCEEDING' OR STATUS = 'RESERVED' OR STATUS = 'SUCCESS' ) ORDER BY START_DATE DESC

SELECT mc.CHALLENGE_ID AS challengeId, ch.DESCRIPTION AS description, ch.TARGET AS target, ch.TARGET_COUNT AS targetCount, ch.DURATION AS duration, ch.CATEGORY AS category, ch.TYPE AS type, mc.STATUS AS status, mc.START_DATE AS startDate, mc.END_DATE AS endDate FROM MYCHALL mc JOIN CHALLENGES ch ON mc.CHALLENGE_ID = ch.CHALLENGE_ID WHERE mc.USER_ID = #{userId}
AND ch.CHALLENGE_MODE = #{challengeMode}
AND ( mc.END_DATE < TRUNC(SYSDATE) OR mc.STATUS IN ('SUCCESS', 'FAILED') ) ORDER BY mc.END_DATE DESC

mapper.update:
UPDATE MYCHALL
SET STATUS = #{status}
WHERE USER_ID = #{userId}
  AND CHALLENGE_ID = #{challengeId}
  AND TRUNC(START_DATE) = TRUNC(#{startDate})
  AND TRUNC(END_DATE) = TRUNC(#{endDate})

mapper.select:
SELECT TO_CHAR(TRUNC(TRANS_DATE), 'YYYY-MM-DD') AS day, NVL(SUM(ORIGINAL_AMOUNT), 0) AS sum FROM MYTRANS WHERE USER_ID = #{userId} AND TYPE = 'OUT' AND TRUNC(TRANS_DATE) >= TO_DATE(#{fromDate}, 'YYYY-MM-DD') AND TRUNC(TRANS_DATE) <= TO_DATE(#{toDate}, 'YYYY-MM-DD')
AND CATEGORY = #{category}
GROUP BY TRUNC(TRANS_DATE) ORDER BY TRUNC(TRANS_DATE)
